INSERT INTO KATEGORIJA
    (ID, PAVADINIMAS, APRASYMAS)
VALUES
    (1, 'Vidutinio stiprumo', 'Dauguma vidutinio stiprumo kokteilių paruošiami be sulčių ar sirupų. jie nėra sudėtingi ir kaloringi. Tinka prie valgio.'),
    (2, 'Gaivieji ir vaisiniai', 'Daugumai kokteilių daugiausiai naudojami švieži vaisiai, ypatingai rūgštūs vaisiai, kaip obuoliai, greipfrutai, citrinos ar pasifloros.'),
    (3, 'Saldieji ir kreminiai', 'Daugiausiai saldžiuose gėrimuose naudojamas kokosų ar ananasų sirupas bei sultys. Dažnai galima rasti ir grietinėlės.'),
    (4, 'Stiprieji', 'Stiprieji gėrimai turi didžiausią alkoholio koncentraciją, dažniausiai, tai kelių stiprių alkoholinių gėrimų rušys.'),
    (5, 'Vieno guršnio', 'Mažo tūrio ypač stiprūs kokteiliai.'),
    (6, 'Šampano', 'Gėrimai, kūrie yra maišomi su prancūzišku šampanu, vokišku (sekt) ar itališku (prosecco) putojančiu vynu.'),
    (7, 'Ilgai geriamas', 'Dažniausiai alkoholinis gėrimas sumaišomas su nealkoholiniu, ypač su apelsinų sultimis arba sodos vandeniu.'),
    (8, 'Rūgštūs ir putojantys', 'Priešingybė saldiesiems kokteiliams, dažnai šampano kokteilis sumaišomas su gazuotu vandeniu.'),
    (9, 'Kavos ir šokolado', 'Dauguma kokteilių kavos ar šokolado pagrindo yra ruošiami karšti, bet yra ir keletas šaltų. Dažniausiai naudojama stipri esspresso kavos rūšis.'),
    (10, 'Kokteiliai kiekvienam', 'Gėrimai skirti visoms amžiaus grupėms, dažniausiai nealkoholinė alternatyva populiariems alkoholiniams gėrimams.');